-- DB Version erh√∂hen
UPDATE ingrid_lookup SET `item_value` = '3.1.0', `item_date` = NOW() WHERE `ingrid_lookup`.`item_key` ='ingrid_db_version' LIMIT 1 ;

-- reorder main menu
UPDATE folder_menu SET `ELEMENT_ORDER` = '1' WHERE OPTIONS ="/main-environment.psml" LIMIT 1 ;
UPDATE folder_menu SET `ELEMENT_ORDER` = '3' WHERE OPTIONS ="/main-service.psml" LIMIT 1 ;
UPDATE folder_menu SET `ELEMENT_ORDER` = '5' WHERE OPTIONS ="/main-maps.psml" LIMIT 1 ;
UPDATE folder_menu SET `ELEMENT_ORDER` = '6' WHERE OPTIONS ="/main-chronicle.psml" LIMIT 1 ;
UPDATE folder_menu SET `ELEMENT_ORDER` = '7', OPTIONS = '/main-features.psml' WHERE OPTIONS ="/main-about.psml" LIMIT 1 ;
UPDATE folder_menu SET `ELEMENT_ORDER` = '8' WHERE OPTIONS ="/administration" LIMIT 1 ;
UPDATE folder_menu SET `ELEMENT_ORDER` = '9' WHERE OPTIONS ="/Administrative" LIMIT 1 ;

-- move catalog menu to main menu
UPDATE folder_menu SET `PARENT_ID` = '19', `ELEMENT_ORDER` = '4' WHERE `folder_menu`.`OPTIONS` ="/search-catalog/search-catalog-hierarchy.psml" LIMIT 1 ;

-- change sitemap from two-column to one-column layout
UPDATE fragment SET `NAME` = 'jetspeed-layouts::IngridOneColumn' WHERE `PAGE_ID` =(SELECT PAGE_ID FROM page WHERE PATH ="/service-sitemap.psml");

-- Temp Table um values aus folder_menu zwischen zu speichern (subselect in insert auf gleiche Tabelle nicht moeglich, s.u.)
DROP TABLE IF EXISTS ingrid_temp;
CREATE TABLE ingrid_temp (
  temp_key varchar(255) NOT NULL,
  temp_value mediumint
);

